
public class IncrementAndDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
		//Increment oprators
		   int x = 5;
		    ++x;
		    System.out.println("This is Increment="+x);
		    
		    
		    //Decremnt Oprators
		    int y = 5;
		    --y;
		    System.out.println("This is Decrement="+y);

	}

}
