
public class Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 5;
	    int b = 3;
	    System.out.println(a > b); // returns true because 5 is greater than 3
	    
	    int c = 5;
	    int d = 3;
	    System.out.println(c < d); // returns false because 5 is not less than 3
	  
	    
	    int p = 5;
	    int q = 3;
	    System.out.println(p >= q); // returns true because 5 is greater, or equal, to 3
	    
	    int x = 5;
	    int y = 3;
	    System.out.println(x <= y); // returns false because 5 is neither less than or equal to 3

	}

}
